class dataInstallTaxMetas{

    public $tax_numbers;
    public $tax_strings;

    function addToWP(){
        global $wpdb;
        $prefix = $wpdb->prefix;

        @mysql_query( "DELETE FROM `".$prefix."gen_options` WHERE `namespace` LIKE 'cm-cat-opt-%'" );
        @mysql_query( "DELETE FROM `".$prefix."gen_options` WHERE `namespace` LIKE 'cm-sin-opt-%'" );

        $values = array();

        // taxonomies meta numbers
        foreach ($this->tax_numbers as $key=>$data) {
            $tax = ffGetByTitle::term( $data['ns_2'] );
            $values[] = " ('".$data['name']."', '".$data['value']."', '".$data['ns_1'].$tax."') ";
        }

        foreach ($this->tax_strings as $key=>$data) {
            $values[] = " ('".$data['name']."', '".$data['value']."', '".$data['namespace']."')" ;
        }

        mysql_query( "INSERT INTO `".$prefix."gen_options` (`name`, `value`, `namespace`)
                            VALUES " . implode(" , ", $values) );
    }

